// 
// 액션은 디스패치의 내용을 수령한다. 
// state는 처음에 보낸 초기값이 담겨있음으로 불변성을 지켜야한다. 

const reducer = (state, action)=> {
  switch(action.type) {
   case 'ADD_MENU':
     return [...state, action.payload]
     case 'DELETE_MENU':
     return  state.ingredients.filter((item) => item.id !== action.payload);
   default:
     return state;
  }
 }


export default reducer
