import { useRef, useState } from "react";

function Q2() {
  const arr = useRef([])
  const [forceRender, setForceRender] = useState(false);
  const [inputValue, setInputValue] = useState("")


  const onAddList = () => {
    setForceRender((prev) => !prev);
    arr.current.push(inputValue);
    // console.log(arr.current)
    // console.log(inputValue)
    setInputValue("")
  };

    const arrList = arr.current.map((arrs) => <li>{arrs}</li>)

  
  
  return (
    <>
      <h1>문제2</h1>
      <div>
        <h2>문제 2-1</h2>
        <p>
          <input 
            type="text"
            value={inputValue} //  input 값 초기화 
            onChange={ e => setInputValue(e.target.value)}
          />
        </p>
        <p>
          <button onClick={onAddList}>추가</button>
        </p>
        <p>
          { {forceRender} ? <div>{inputValue} </div> : <div>{inputValue} </div> }
          <button >제출</button>
        </p>

        <p>{ forceRender === true ?  `제출된 목록이 없습니다` : ``}</p>
        <ul>{arrList}</ul>
      </div>
      <div>
        <h2>문제 2-2</h2>
        <p> 이 문구는 아래 버튼을 누르면 색상이 바뀝니다</p>
        <button>변경</button>
      </div>
    </>
  );
}
export default Q2;


 /* 
    문제2

    2-1)
        useRef에 관련한 문제입니다.

        // 강게제런더링 시켜주는 state하나 
        // 

        추가 버튼을 누르면 input에 있던 value는 배열 arr에 추가됩니다.
        그러나, 추가 버튼을 누를 때마다 강제 랜더링 상태가 무조건 적으로 업데이트 됩니다.

        이러한 상황에서 제출버튼을 누르면

        지금까지 추가하였던 목록 배열(arr)이 
        <ul>의 li의 항목으로 추가되어야합니다.  
      

        만약 제출되었을 때 아무런 항목이 없다면
        <p>제출된 목록이 없습니다</p>이 노출되어야하며

        제출된 항목이 있다면
        <ul>만 노출되어야 합니다

        이를 useRef의 특성을 고려하여 풀이해보세요 :)

    2-2)
        문제 2-2는 변경 버튼을 클릭하면
        p태그의 색상이 다른 색상으로 변경됩니다.
        
        이는 state를 사용하는 것이 가장 올바른 방법이지만
        어를 사용할 수 없는 어쩔 수 없는 상황에 놓여있습니다.

        따라서 useRef는 사용하여 해당 문구의 색상을 변경해보세요 :)
  */
