import { useRef, useState } from "react";


// 강제 렌더링의 중요한 점 :  
//  setForceRender((prev) => !prev);로 값이 변화해서 상태값이 변화하면 리렌더 발생한다. 
//  함수가 처음부터 다시 실행된다. 
//  다만 함수 실행(리렌더)만된다는거지 화면 노출까지 할려면 retrun에 실행할 결과를 전달해야한다. 


function Q2() {
  const arr = useRef([])
  const [forceRender, setForceRender] = useState(false);
  const [list, useLIst] = useState()
  const inputRef = useRef("")  // inputRef라는 객체 생성 초기값을 "" 
  
  const onAddList = () => {
    const inputValue = inputRef.current.value;
    arr.current.push(inputValue);
    setForceRender((prev) => !prev); // true로 값이 변경 함수 재실행 변수 초기화 / ref값은 유지 
    // console.log(arr.current)
    // console.log(inputValue)  
    inputRef.current.value = "";
  };

  
  const onClick = () =>{
    if(forceRender){
      return arr.current.map((arrs,index) => <li key={index}>{arrs}</li>)
    } else{
      return <p>제출된 목록이 없습니다.</p>
    }
  }

  // (forceRender &&  arr.current.map((arrs,index) => <li key={index}>{arrs}</li>))
  return (
    <>
      <h1>문제2</h1>
      <div>
        <h2>문제 2-1</h2>
        <p> 
          <input 
            type="text"
            ref={inputRef}  // 초기값 ""배열을 전달하며 input 태그와 연결 
          />
        </p>
        <p>
          <button onClick={onAddList}>추가</button>
        </p>
        <p>
          <button onClick={onClick}>제출</button>
        </p>
        {/* <p>{ forceRender === true ?  `` : `제출된 목록이 없습니다`}</p> */}
        <ul>{forceRender && onClick()}</ul>
      </div>
      <div>
        <h2>문제 2-2</h2>
        <p> 이 문구는 아래 버튼을 누르면 색상이 바뀝니다</p>
        <button>변경</button>
      </div>
    </>
  );
}
export default Q2;



